#!/usr/bin/env python3 """ Check Requirements for Conversational API Integration Checks if all required packages are installed for the conversational API system. """ import sys import subprocess def check_package(package_name, import_name=None): """Check if a package is installed.""" if import_name is None: import_name = package_name try: __import__(import_name) print(f" {package_name}: Installed") return True except ImportError: print(f" {package_name}: Not installed") return False def install_package(package_name): """Install a package using pip.""" try: subprocess.check_call([sys.executable, "-m", "pip", "install", package_name]) print(f" Successfully installed {package_name}") return True except subprocess.CalledProcessError: print(f" Failed to install {package_name}") return False def main(): print(" Checking Requirements for Conversational API Integration") print("=" * 60) # Required packages for conversational API integration required_packages = [ ("torch", "torch"), ("numpy", "numpy"), ("requests", "requests"), ("datasets", "datasets"), # Hugging Face datasets ("transformers", "transformers"), # Optional but recommended ] missing_packages = [] print(" Checking installed packages...") for package_name, import_name in required_packages: if not check_package(package_name, import_name): missing_packages.append(package_name) if missing_packages: print(f"\n Missing packages: {', '.join(missing_packages)}") print(f"\n Installation commands:") for package in missing_packages: print(f" pip install {package}") # Ask if user wants to install automatically print(f"\n Would you like to install missing packages automatically? (y/n)") try: response = input().lower().strip() if response in ['y', 'yes']: print(f"\n Installing missing packages...") for package in missing_packages: print(f"\n Installing {package}...") install_package(package) except KeyboardInterrupt: print(f"\n‚èπ Installation cancelled") else: print(f"\n All required packages are installed!") # Check specific versions print(f"\n Package versions:") try: import torch print(f" torch: {torch.__version__}") except: pass try: import numpy print(f" numpy: {numpy.__version__}") except: pass try: import datasets print(f" datasets: {datasets.__version__}") except: pass try: import transformers print(f" transformers: {transformers.__version__}") except: pass # Check CUDA availability try: import torch if torch.cuda.is_available(): print(f"\n CUDA available: {torch.cuda.get_device_name(0)}") else: print(f"\n CUDA not available, using CPU") except: pass print(f"\n Next steps:") if not missing_packages: print(f" Run: python test_hf_token.py") print(f" Then: python examples/real_huggingface_ingestion.py") else: print(f" Install missing packages first") print(f" Then re-run this check") if __name__ == "__main__": main()